const r=document.querySelectorAll("button.copy");let c;r.forEach(e=>{e.addEventListener("click",async()=>{const o=decodeURIComponent(e.value);try{clearTimeout(c),await navigator.clipboard.writeText(o),e.classList.add("copied"),c=setTimeout(()=>{e.classList.remove("copied")},3e3)}catch{clearTimeout(c),e.classList.add("copy-error"),c=setTimeout(()=>{e.classList.remove("copy-error")},3e3)}})});
